"use strict";

window.onscroll = function () {
  var scrolled = window.pageYOffset || document.documentElement.scrollTop;
  var header = document.getElementById('header');

  if (scrolled < 50) {
    header.classList.remove('scroll');
  } else {
    header.classList.add('scroll');
  }
};
